import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Form from './Form';
import Contacts from './Contacts';
import Filter from './Filter';

class PhoneBook extends Component {
  state = {
    contacts: [],
    name: '',
    filter: '',
  };

  addToList = contact => {
    this.setState(prev => ({ contacts: [...prev.contacts, contact] }));
  };

  filterContact = () => {
    const { name, filter } = this.state;
    return name.filter(names => names.filter.toLowerCase().includes(filter.toLowerCase()));
  };

  deleteContact = id => {
    this.setState({
      contacts: this.state.contacts.filter(contact => contact.id !== id),
    });
  };

  render() {
    const { filter } = this.state;
    const visibleUser = this.filterUser();
    return (
      <div>
        <h1>PhoneBook</h1>
        <Form addToList={this.addToList} />
        <Filter value={filter} />
        <Contacts contacts={this.state.contacts} />
      </div>
    );
  }
}

PhoneBook.propTypes = {};

export default PhoneBook;
