import PropTypes from 'prop-types';

class Form extends Component {
  state = {
    name: '',
    phone: '',
  };

  inputHandler = ({ target }) => {
    const { value, name, } = target;
    this.setState({
      [name]: type === 'checkbox' ? !this.state.agree : value,
    });
  };
  render() {
    return (
      <form>
        <input placeholder="Enter name" type="text" name="name"></input>
        <input placeholder="Enter phone" type="text" name="phone"></input>
        <button type="submit">Add contacts</button>
      </form>
    );
  }
}

Form.propTypes = {};

export default Form;
